import React from "react";
import { Col, Row } from "react-bootstrap";
import { TaskList } from "./TaskList";

export const ListArea = ({ tasks }) => {
  //tasks is used in filtering
  const entryList = tasks.filter((task) => task.type === "entry"); //entryList is the filtered list of tasks
  const badList = tasks.filter((task) => task.type === "bad");
  return (
    <div className="list-area">
      <Row>
        <Col>
          <TaskList title="Entry List" arrow="right" list={entryList} />
          {/* entryList is  */}
        </Col>

        <Col>
          <TaskList title="Bad List" list={badList} />
          <div className="saving text-end text-danger fw-bold">
            You could have saved 40 hrs
          </div>
        </Col>
      </Row>
      <div className="text-center fw-bold">
        Total hours allocated is 50 hrs per week{" "}
      </div>
    </div>
  );
};
